CREATE TABLE ENTRENADOR (
  ID_ENTRENADOR int(11) NOT NULL,
  NOM_ENTRENADOR varchar(20) NOT NULL,
  PASSWORD varchar(20) NOT NULL,
  POKEDOLARES decimal(17,0),
  CONSTRAINT ENT_PK PRIMARY KEY(ID_ENTRENADOR)
);


CREATE TABLE POKEDEX (
  NUM_POKEDEX int(11) NOT NULL,
  NOM_POKEMON varchar(30) NOT NULL,
  TIPO1 varchar(20) NOT NULL,
  TIPO2 varchar(20),
  IMAGEN_FRONT varchar(100) NOT NULL,
  IMAGEN_BACK varchar(100) NOT NULL,
  SONIDO varchar(100) NOT NULL,
  NIVEL_EVOLUCION int(11),
  NUM_POKEDEX_EVO int(11),
  CONSTRAINT PKX_PK PRIMARY KEY(NUM_POKEDEX)
);

COMMIT;
-- Y así sucesivamente para los demás Pokémon de la primera generación

UPDATE pokedex
SET IMAGEN_FRONT = CONCAT(CONCAT('Pokemon/Front/',NUM_POKEDEX),'.png');
UPDATE pokedex
SET IMAGEN_BACK = CONCAT(CONCAT('Pokemon/Back/',NUM_POKEDEX),'.png');

UPDATE pokedex
SET SONIDO = CONCAT(CONCAT('Pokemon/00',NUM_POKEDEX),'.wav')
WHERE NUM_POKEDEX<10;

UPDATE pokedex
SET SONIDO = CONCAT(CONCAT('Pokemon/0',NUM_POKEDEX),'.wav')
WHERE NUM_POKEDEX >=10 AND NUM_POKEDEX<100;

UPDATE pokedex
SET SONIDO = CONCAT(CONCAT('Pokemon/',NUM_POKEDEX),'.wav')
WHERE NUM_POKEDEX>=100;


CREATE TABLE POKEMON (
  ID_POKEMON int(11) NOT NULL,
  ID_ENTRENADOR int(11),
  NUM_POKEDEX int(11),
  MOTE varchar(20),
  CAJA int(11) NOT NULL,
  ATAQUE int(11) NOT NULL,
  ATA_ESPECIAL int(11) NOT NULL,
  DEFENSA int(11) NOT NULL,
  DEF_ESPECIAL int(11) NOT NULL,
  VELOCIDAD int(11) NOT NULL,
  NIVEL int(11) NOT NULL,
  FERTILIDAD int(2) NOT NULL,
  SEXO VARCHAR(1) NOT NULL,
  ESTADO VARCHAR(20) NOT NULL,
  EXPERIENCIA INT NOT NULL,
  VITALIDAD INT NOT NULL,
  VITALIDAD_MAXIMA INT NOT NULL,
  CONSTRAINT POK_PK PRIMARY KEY(ID_POKEMON)
);





ALTER TABLE POKEMON
ADD CONSTRAINT PKX_IDENT_FK FOREIGN KEY ID_ENTRENADOR REFERENCES ENTRENADOR(ID_ENTRENADOR);

ALTER TABLE POKEMON
ADD CONSTRAINT PKX_NUMPKX_FK FOREIGN KEY NUM_POKEDEX REFERENCES POKEDEX(NUM_POKEDEX);

